package org.codecover.simplejavaapp.view;

import java.awt.BorderLayout;
import java.awt.Color;
import java.awt.event.ActionEvent;
import java.awt.event.InputEvent;
import java.awt.event.KeyEvent;
import java.awt.event.WindowAdapter;
import java.awt.event.WindowEvent;
import java.util.List;

import javax.swing.AbstractAction;
import javax.swing.Action;
import javax.swing.ImageIcon;
import javax.swing.JButton;
import javax.swing.JFrame;
import javax.swing.JMenu;
import javax.swing.JMenuBar;
import javax.swing.JMenuItem;
import javax.swing.JScrollPane;
import javax.swing.JTable;
import javax.swing.JToolBar;
import javax.swing.KeyStroke;
import javax.swing.table.AbstractTableModel;

import org.codecover.simplejavaapp.model.AppModel;
import org.codecover.simplejavaapp.model.Book;
import org.codecover.simplejavaapp.model.AppModel.AppModelModifyListener;

/**
 * @author Markus Wittlinger
 * @version 1.0 ($Id$)
 */
@SuppressWarnings("serial")
public class FrameMain extends JFrame {
    private final String[] colNames = new String[] {"Author",
                                                    "Name",
                                                    "Year",
                                                    "ISBN"};

    private static final AppModel model = AppModel.getInstance();

    private static final AppView view = AppView.getInstance();

    private String fileId;

    private JTable table;

    private final AppAction newWindowAction = new AppAction("New Window", null,
            "Creates a new file and opens it in a new window.", KeyStroke
                    .getKeyStroke('N', InputEvent.META_DOWN_MASK)) {

        /**
         * (non-Javadoc)
         * 
         * @see java.awt.event.ActionListener#actionPerformed(java.awt.event.ActionEvent)
         */
        public void actionPerformed(ActionEvent e) {
            view.notifyViewListener(
                    AppView.ViewListener.ViewEvent.EVENT_NEW_WINDOW,
                    FrameMain.this.fileId, null);
        }
    };

    private final AppAction newBookAction = new AppAction("New Book",
            new ImageIcon("icons/new.png"), "Creates a new book.", KeyStroke
                    .getKeyStroke('N', InputEvent.META_DOWN_MASK
                            | InputEvent.SHIFT_DOWN_MASK)) {

        public void actionPerformed(ActionEvent e) {
            view.notifyViewListener(
                    AppView.ViewListener.ViewEvent.EVENT_NEW_BOOK,
                    FrameMain.this.fileId, null);
        }
    };

    private final AppAction closeWindowAction = new AppAction("Close Window",
            null, "Closes the window.", KeyStroke.getKeyStroke('W',
                    InputEvent.META_DOWN_MASK)) {

        public void actionPerformed(ActionEvent e) {
            view.notifyViewListener(
                    AppView.ViewListener.ViewEvent.EVENT_CLOSE_WINDOW,
                    FrameMain.this.fileId, null);
        }
    };

    private final AppAction deleteAction = new AppAction("Delete",
            new ImageIcon("icons/delete.png"), "Deletes the selected element.",
            KeyStroke.getKeyStroke(KeyEvent.VK_DELETE, 0)) {

        public void actionPerformed(ActionEvent e) {
            view.notifyViewListener(
                    AppView.ViewListener.ViewEvent.EVENT_DELETE,
                    FrameMain.this.fileId, FrameMain.this.getSelectedBooks());
        }
    };

    private final AppAction openAction = new AppAction("Open‚Ä¶", null,
            "Opens a previosly saved file.", KeyStroke.getKeyStroke('O',
                    InputEvent.META_DOWN_MASK)) {

        public void actionPerformed(ActionEvent e) {
            view.notifyViewListener(AppView.ViewListener.ViewEvent.EVENT_OPEN,
                    FrameMain.this.fileId, null);
        }
    };

    private final AppAction quitAction = new AppAction("Quit", null,
            "Closes the application", KeyStroke.getKeyStroke('Q',
                    InputEvent.META_DOWN_MASK)) {

        public void actionPerformed(ActionEvent e) {
            view.notifyViewListener(AppView.ViewListener.ViewEvent.EVENT_QUIT,
                    FrameMain.this.fileId, FrameMain.view.getAllFileIds());
        }
    };

    private final AppAction saveAction = new AppAction("Save", new ImageIcon(
            "icons/save.png"), "Saves the current file.", KeyStroke
            .getKeyStroke('S', InputEvent.META_DOWN_MASK)) {

        public void actionPerformed(ActionEvent e) {
            view.notifyViewListener(AppView.ViewListener.ViewEvent.EVENT_SAVE,
                    FrameMain.this.fileId, null);
        }
    };

    private final AppAction saveAsAction = new AppAction("Save As‚Ä¶",
            new ImageIcon("icons/save.png"),
            "Saves the current file to the specified location.", KeyStroke
                    .getKeyStroke('S', InputEvent.META_DOWN_MASK
                            | InputEvent.SHIFT_DOWN_MASK)) {

        public void actionPerformed(ActionEvent e) {
            view.notifyViewListener(
                    AppView.ViewListener.ViewEvent.EVENT_SAVE_AS,
                    FrameMain.this.fileId, null);
        }
    };

    final AppModelModifyListener appModelModifyListener = new AppModelModifyListener() {
        public void appFileModified(String fileId, boolean modified) {
            FrameMain.this.updateTitle(modified);
            FrameMain.this.table.setModel(new BookTableModel());
        }
    };

    FrameMain(String title, String fileId) {
        super(title == null ? "Untitled" : title);
        this.setDefaultCloseOperation(DO_NOTHING_ON_CLOSE);
        this.addWindowListener(new WindowAdapter() {
            @Override
            public void windowClosing(WindowEvent e) {
                FrameMain.view.notifyViewListener(
                        AppView.ViewListener.ViewEvent.EVENT_CLOSE_WINDOW,
                        FrameMain.this.fileId, null);
            }
        });
        this.fileId = fileId;

        this.setSize(600, 500);
        this.setLocationRelativeTo(null);

        initComponents();
    }

    private final void initComponents() {
        this.setLayout(new BorderLayout());

        createToolbar();
        createMenuBar();

        this.table = new JTable(new BookTableModel());
        this.table.setShowGrid(true);
        this.table.setGridColor(Color.LIGHT_GRAY);
        JScrollPane scrollPane = new JScrollPane(this.table);

        this.add(scrollPane, BorderLayout.CENTER);


        model.addAppModelModifyListenerToFile(this.fileId,
                this.appModelModifyListener);
    }

    private final void createToolbar() {
        JToolBar toolBar = new JToolBar("Toolbar");
        toolBar.setFloatable(false);
        toolBar.setRollover(true);
        this.add(toolBar, BorderLayout.NORTH);

        final JButton newWindowButton = new JButton(this.newBookAction);
        newWindowButton.setText("");
        toolBar.add(newWindowButton);

        final JButton savebutton = new JButton(this.saveAction);
        savebutton.setText("");
        toolBar.add(savebutton);

        toolBar.addSeparator();

        final JButton deleteButton = new JButton(this.deleteAction);
        deleteButton.setText("");
        toolBar.add(deleteButton);
    }

    private final void createMenuBar() {
        JMenuBar menuBar = new JMenuBar();
        this.setJMenuBar(menuBar);

        JMenu fileMenu = new JMenu("File");
        menuBar.add(fileMenu);
        fileMenu.add(new JMenuItem(this.newWindowAction));

        final JMenuItem newBookItem = new JMenuItem(this.newBookAction);
        newBookItem.setIcon(null);
        fileMenu.add(newBookItem);

        fileMenu.add(new JMenuItem(this.openAction));
        fileMenu.add(new JMenuItem(this.closeWindowAction));
        fileMenu.addSeparator();

        final JMenuItem saveItem = new JMenuItem(this.saveAction);
        saveItem.setIcon(null);
        fileMenu.add(saveItem);

        final JMenuItem saveAsItem = new JMenuItem(this.saveAsAction);
        saveAsItem.setIcon(null);
        fileMenu.add(saveAsItem);

        fileMenu.addSeparator();
        fileMenu.add(new JMenuItem(this.quitAction));

        JMenu editMenu = new JMenu("Edit");
        menuBar.add(editMenu);

        final JMenuItem deleteItem = new JMenuItem(this.deleteAction);
        deleteItem.setIcon(null);
        editMenu.add(deleteItem);
    }

    private List<Book> getBookList() {
        return model.getBooksInFile(FrameMain.this.fileId);
    }

    private String[] getSelectedBooks() {
        List<Book> bookList = getBookList();
        int[] indices = this.table.getSelectedRows();
        String[] bookIds = new String[indices.length];
        for (int i = 0; i < indices.length; i++) {
            bookIds[i] = bookList.get(indices[i]).getId();
        }

        return bookIds;
    }

    private void updateTitle(boolean modified) {
        String title = (model.getPathOfFile(this.fileId) == null ? "Untitled"
                : model.getPathOfFile(this.fileId));

        if (modified) {
            title += "*";
        }

        setTitle(title);
    }

    /**
     * Sets the file id.
     * 
     * @param fileId
     *            the given file id.
     */
    public void setFileId(String fileId) {
        model.removeAppModelModifyListener(this.fileId,
                this.appModelModifyListener);
        this.fileId = fileId;
        model.addAppModelModifyListenerToFile(this.fileId,
                this.appModelModifyListener);
        this.updateTitle(false);
        this.table.updateUI();
    }

    private abstract class AppAction extends AbstractAction {
        /**
         * Constructor.
         * 
         * @param name
         *            the name of the action.
         * @param icon
         *            the icon of the action.
         * @param desc
         *            the description of the action.
         * @param keyStroke
         *            the keystroke of the action.
         */
        protected AppAction(String name, ImageIcon icon, String desc,
                KeyStroke keyStroke) {
            super(name, icon);
            this.putValue(Action.SHORT_DESCRIPTION, desc);
            if (keyStroke != null) {
                this.putValue(Action.ACCELERATOR_KEY, keyStroke);
            }
        }
    }

    private final class BookTableModel extends AbstractTableModel {

        /**
         * (non-Javadoc)
         * 
         * @see javax.swing.table.TableModel#getColumnClass(int)
         */
        @Override
        public Class<?> getColumnClass(int columnIndex) {
            switch (columnIndex) {
                case 0:
                    return String.class;
                case 1:
                    return String.class;
                case 2:
                    return Integer.class;
                case 3:
                    return String.class;
            }
            return null;
        }

        /**
         * (non-Javadoc)
         * 
         * @see javax.swing.table.AbstractTableModel#getColumnName(int)
         */
        @Override
        public String getColumnName(int column) {
            return FrameMain.this.colNames[column];
        }

        /**
         * (non-Javadoc)
         * 
         * @see javax.swing.table.TableModel#getColumnCount()
         */
        public int getColumnCount() {
            return FrameMain.this.colNames.length;
        }

        /**
         * (non-Javadoc)
         * 
         * @see javax.swing.table.TableModel#getRowCount()
         */
        public int getRowCount() {
            return getBookList().size();
        }

        /**
         * (non-Javadoc)
         * 
         * @see javax.swing.table.TableModel#getValueAt(int, int)
         */
        public Object getValueAt(int rowIndex, int columnIndex) {
            Book book = getBookList().get(rowIndex);

            switch (columnIndex) {
                case 0:
                    return book.getAuthor();
                case 1:
                    return book.getName();
                case 2:
                    return book.getYear();
                case 3:
                    return book.getIsbn();
            }
            return null;
        }

        /**
         * (non-Javadoc)
         * 
         * @see javax.swing.table.TableModel#isCellEditable(int, int)
         */
        @Override
        public boolean isCellEditable(int rowIndex, int columnIndex) {
            return true;
        }

        /**
         * (non-Javadoc)
         * 
         * @see javax.swing.table.TableModel#setValueAt(java.lang.Object, int,
         *      int)
         */
        @Override
        public void setValueAt(Object value, int rowIndex, int columnIndex) {
            Book book = getBookList().get(rowIndex);

            switch (columnIndex) {
                case 0:
                    book.setAuthor((String) value);
                    break;
                case 1:
                    book.setName((String) value);
                    break;
                case 2:
                    book.setYear(((Integer) value).intValue());
                    break;
                case 3:
                    book.setIsbn((String) value);
                    break;
            }
        }
    }
}

package org.codecover.simplejavaapp.view;

import java.util.HashMap;
import java.util.LinkedList;
import java.util.List;
import java.util.Map;
import java.util.Set;

import javax.swing.JFileChooser;
import javax.swing.JOptionPane;

import org.codecover.simplejavaapp.controller.AppController;
import org.codecover.simplejavaapp.model.AppFile;
import org.codecover.simplejavaapp.model.AppModel;
import org.codecover.simplejavaapp.view.AppView.ViewListener.ViewEvent;

/**
 * @author Markus Wittlinger
 * @version 1.0 ($Id$)
 */
public class AppView {
    private static final AppModel model = AppModel.getInstance();

    private static AppView instance;

    private final List<ViewListener> listeners;

    private final Map<String, FrameMain> windowList;

    /**
     * Singleton-Pattern method to get the instance of {@link AppView}.
     * 
     * @return the instance of the {@link AppView}
     */
    public static AppView getInstance() {
        if (instance == null) {
            instance = new AppView();
        }

        return instance;
    }

    private AppView() {
        this.windowList = new HashMap<String, FrameMain>();
        this.listeners = new LinkedList<ViewListener>();
    }

    void notifyViewListener(ViewEvent viewEvent, String fileId, Object args) {
        for (ViewListener listener : this.listeners) {
            listener.update(viewEvent, fileId, args);
        }
    }

    /**
     * Adds the given {@link ViewListener} to the list of listeners.
     * 
     * @param listener
     *            the given {@link ViewListener}
     */
    public void addViewListener(ViewListener listener) {
        this.listeners.add(listener);
    }

    /**
     * Removes the given {@link ViewListener} from the list of listeners.
     * 
     * @param listener
     *            the given {@link ViewListener}
     */
    public void removeViewListener(ViewListener listener) {
        this.listeners.remove(listener);
    }

    /**
     * Opens a new window with the {@link AppFile}, as denoted by the given id,
     * as its content.
     * 
     * @param fileId
     *            the id of the {@link AppFile}.
     */
    public void openWindow(String fileId) {
        FrameMain frameMain = new FrameMain(model.getPathOfFile(fileId), fileId);

        this.windowList.put(fileId, frameMain);
        frameMain.setVisible(true);
    }

    /**
     * Opens a file with the given id in the window currently still displaying
     * another file.
     * 
     * @param oldFileId
     *            the id of the currently displayed file.
     * @param newFileId
     *            the id of the to be displayed file.
     */
    public void openFileInWindow(String oldFileId, String newFileId) {
        FrameMain frameMain = getWindowFromId(oldFileId);

        this.windowList.remove(oldFileId);

        this.windowList.put(newFileId, frameMain);

        frameMain.setFileId(newFileId);
    }

    /**
     * Closes the window displaying the file with the given id.
     * 
     * @param fileId
     *            the id of the displayed file.
     */
    public void closeWindow(String fileId) {
        FrameMain frameMain = getWindowFromId(fileId);

        this.windowList.remove(fileId);

        frameMain.setVisible(false);
        frameMain.dispose();
    }

    /**
     * Displays the given error message.
     * 
     * @param message
     *            the error message.
     * @param fileId
     *            the id of the currently displayed file.
     */
    public void showErrorDialog(String message, String fileId) {
        JOptionPane.showMessageDialog(getWindowFromId(fileId), message,
                "Error", JOptionPane.ERROR_MESSAGE);
    }

    /**
     * Displays the given error message.
     * 
     * @param message
     *            the error message.
     * @param fileId
     *            the id of the currently displayed file.
     * @return the result
     */
    public int showConfirmationDialog(String message, String fileId) {
        return JOptionPane.showConfirmDialog(getWindowFromId(fileId), message,
                "Please Confirm", JOptionPane.YES_NO_CANCEL_OPTION);
    }

    /**
     * Shows a save as‚Ä¶ dialog.
     * 
     * @param fileId
     *            the id of the currently displayed file.
     * @return the path of the file, or <code>null</code> if the action was
     *         canceled.
     */
    public String showSaveAsDialog(String fileId) {
        JFileChooser chooser = new JFileChooser();
        chooser.setDialogType(JFileChooser.SAVE_DIALOG);
        chooser.setDialogTitle("Save As‚Ä¶");
        chooser.setFileSelectionMode(JFileChooser.FILES_ONLY);

        if (chooser.showSaveDialog(getWindowFromId(fileId)) == JFileChooser.APPROVE_OPTION) {
            return chooser.getSelectedFile().getAbsolutePath();
        }

        return null;
    }

    /**
     * Shows a open dialog.
     * 
     * @param fileId
     *            the id of the currently displayed file.
     * @return the path of the file, or <code>null</code> if the action was
     *         canceled.
     */
    public String showOpenDialog(String fileId) {
        JFileChooser chooser = new JFileChooser();
        chooser.setDialogType(JFileChooser.OPEN_DIALOG);
        chooser.setDialogTitle("Open");
        chooser.setFileSelectionMode(JFileChooser.FILES_ONLY);

        if (chooser.showOpenDialog(getWindowFromId(fileId)) == JFileChooser.APPROVE_OPTION) {
            return chooser.getSelectedFile().getAbsolutePath();
        }

        return null;
    }

    private FrameMain getWindowFromId(String fileId) {
        return this.windowList.get(fileId);
    }

    String[] getAllFileIds() {
        Set<String> idSet = this.windowList.keySet();
        String[] ids = new String[idSet.size()];
        idSet.toArray(ids);

        return ids;
    }

    /**
     * A listener to notify the {@link AppController} of any events, that are
     * relevant to it.
     * 
     * @author Markus Wittlinger
     * @version 1.0 ($Id$)
     */
    public interface ViewListener {
        /**
         * An enum denoting the events the AppView can send.
         * 
         * @author Markus Wittlinger
         * @version 1.0 ($Id$)
         */
        enum ViewEvent {
            /**
             * EVENT_NEW_WINDOW
             */
            EVENT_NEW_WINDOW,
            /**
             * EVENT_NEW_BOOK
             */
            EVENT_NEW_BOOK,
            /**
             * EVENT_OPEN (args contains the ids of the selected books as a
             * String array)
             */
            EVENT_OPEN,
            /**
             * EVENT_CLOSE_WINDOW
             */
            EVENT_CLOSE_WINDOW,
            /**
             * EVENT_SAVE
             */
            EVENT_SAVE,
            /**
             * EVENT_SAVE_AS
             */
            EVENT_SAVE_AS,
            /**
             * EVENT_DELETE
             */
            EVENT_DELETE,
            /**
             * EVENT_QUIT (args contains the ids of the open files as a String
             * array)
             */
            EVENT_QUIT;
        }

        /**
         * Is called, when an event occurs.
         * 
         * @param viewEvent
         *            the type of event
         * @param fileId
         *            the id of the file and windows the event occurred in.
         * @param args
         *            the additional arguments, that are passed.
         */
        public void update(ViewEvent viewEvent, String fileId, Object args);
    }
}

package org.codecover.simplejavaapp.model;

import java.util.UUID;

import org.w3c.dom.Document;
import org.w3c.dom.Element;

/**
 * A model class holding the data of a book. Also contains methods for
 * persistent storage.
 * 
 * @author Markus Wittlinger
 * @version 1.0 ($Id$)
 */
public class Book {
    private static final String YEAR = "year";

    private static final String NAME = "name";

    private static final String ISBN = "isbn";

    private static final String ID = "id";

    private static final String AUTHOR = "author";

    private final String id;

    private String name = "name";

    private String author = "author";

    private int year = 2007;

    private String isbn = "isbn";

    private BookModifyListener listener;

    /**
     * Constructor, which creates a {@link Book} with the given id.
     * 
     * @param id
     *            the id of the {@link Book}
     */
    public Book(String id) {
        this.id = id;
    }

    /**
     * Constructor, which gives this {@link Book} a random id.
     */
    public Book() {
        this(UUID.randomUUID().toString());
    }

    /**
     * Gets the id.
     * 
     * @return the id
     */
    public String getId() {
        return this.id;
    }

    /**
     * Gets the name.
     * 
     * @return the name
     */
    public String getName() {
        return this.name;
    }

    /**
     * Gets the author.
     * 
     * @return the author
     */
    public String getAuthor() {
        return this.author;
    }

    /**
     * Gets the year.
     * 
     * @return the year
     */
    public int getYear() {
        return this.year;
    }

    /**
     * Gets the isbn.
     * 
     * @return the isbn
     */
    public String getIsbn() {
        return this.isbn;
    }

    /**
     * Sets the name.
     * 
     * @param name
     *            the name to set
     */
    public void setName(String name) {
        this.name = name;
        notifiyChange();
    }

    /**
     * Sets the author.
     * 
     * @param author
     *            the author to set
     */
    public void setAuthor(String author) {
        this.author = author;
        notifiyChange();
    }

    /**
     * Sets the year.
     * 
     * @param year
     *            the year to set
     */
    public void setYear(int year) {
        this.year = year;
        notifiyChange();
    }

    /**
     * Sets the isbn.
     * 
     * @param isbn
     *            the isbn to set
     */
    public void setIsbn(String isbn) {
        this.isbn = isbn;
        notifiyChange();
    }

    private void notifiyChange() {
        if (this.listener == null) {
            return;
        }

        this.listener.stateModified();
    }

    /**
     * Sets the {@link BookModifyListener}, that is notified, when a change
     * occurs.
     * 
     * @param listener
     *            the {@link BookModifyListener}
     */
    public void setBookModifyListener(BookModifyListener listener) {
        this.listener = listener;
    }

    /**
     * Return a DOM {@link Element}, which holds all the data of this instance
     * of {@link Book}.
     * 
     * @param xmlDoc
     *            the document, in which the {@link Element} is to be created.
     * @return the created {@link Element}.
     */
    public Element getXMLRepresentation(Document xmlDoc) {
        if (xmlDoc == null) {
            throw new NullPointerException("xmlDoc == null");
        }

        Element bookElement = xmlDoc.createElement("Book");

        bookElement.setAttribute(AUTHOR, getAuthor());
        bookElement.setAttribute(ID, getId());
        bookElement.setAttribute(ISBN, getIsbn());
        bookElement.setAttribute(NAME, getName());
        bookElement.setAttribute(YEAR, Integer.toString(getYear()));

        return bookElement;
    }

    /**
     * Gets the book, that is contained this the given DOM {@link Element}
     * 
     * @param element
     *            the DOM {@link Element} holding the data.
     * @return the extracted {@link Book}
     */
    public static Book getBookFromXML(Element element) {
        String author = element.getAttribute(AUTHOR);
        String id = element.getAttribute(ID);
        String isbn = element.getAttribute(ISBN);
        String name = element.getAttribute(NAME);
        String year = element.getAttribute(YEAR);

        Book book = new Book(id);

        book.setAuthor(author);
        book.setIsbn(isbn);
        book.setName(name);
        book.setYear(Integer.parseInt(year));

        return book;
    }

    /**
     * A listener to monitor changes in the {@link Book}.
     * 
     * @author Markus Wittlinger
     * @version 1.0 ($Id$)
     */
    public interface BookModifyListener {
        /**
         * Is called every time a property of the book is changed.
         */
        public void stateModified();
    }
}

package org.codecover.simplejavaapp.model;

import java.io.File;
import java.io.IOException;
import java.util.Collection;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

import javax.xml.parsers.ParserConfigurationException;
import javax.xml.transform.TransformerException;

import org.xml.sax.SAXException;

/**
 * @author Markus Wittlinger
 * @version 1.0 ($Id$)
 */
public class AppModel {
    private static AppModel instance;

    private final Map<String, AppFile> appFiles;

    /**
     * Singleton-Pattern method to get the instance of {@link AppModel}.
     * 
     * @return the instance of the {@link AppModel}
     */
    public static AppModel getInstance() {
        if (instance == null) {
            instance = new AppModel();
        }

        return instance;
    }

    private AppModel() {
        this.appFiles = new HashMap<String, AppFile>();
    }

    /**
     * Adds a new {@link AppFile} to the model and returns its id.
     * 
     * @return the id of the {@link AppFile}.
     */
    public String newFile() {
        AppFile appFile = AppFile.newFile();
        putAppFile(appFile);

        return appFile.getId();
    }

    /**
     * Loads an {@link AppFile} from the given location.
     * 
     * @param file
     *            the location of the persistently saved {@link AppFile}
     * @return the read {@link AppFile}.
     * @throws ParserConfigurationException
     * @throws SAXException
     * @throws IOException
     */
    public String loadFile(File file) throws ParserConfigurationException,
            SAXException, IOException {
        if (file == null) {
            throw new NullPointerException("file == null");
        }

        AppFile appFile = AppFile.loadAppFile(file);
        putAppFile(appFile);

        return appFile.getId();
    }

    /**
     * Saves the {@link AppFile} with the given id as a XML file to the given
     * location.
     * 
     * @param fileId
     *            the id of the {@link AppFile}
     * @param file
     *            the given location.
     * @throws ParserConfigurationException
     * @throws TransformerException
     * @throws IOException
     */
    public void saveFile(String fileId, File file)
            throws ParserConfigurationException, TransformerException,
            IOException {
        if (file == null) {
            throw new NullPointerException("file == null");
        }

        AppFile appFile = getAppFile(fileId);

        appFile.saveAppFile(file);
    }

    /**
     * Closes the {@link AppFile} with the given id.
     * 
     * @param fileId
     *            the id of the {@link AppFile}.
     * @param force
     *            if this is set to true, the file is closed even with
     *            modification
     * @return true &rarr; the {@link AppFile} was not modified and is now
     *         closed. </br> false &rarr; the {@link AppFile} was modified and
     *         is still open.
     */
    public boolean closeFile(String fileId, boolean force) {
        if (isFileModified(fileId) && !force) {
            return false;
        }

        this.appFiles.remove(fileId);
        return true;
    }

    /**
     * @param appFile
     */
    private void putAppFile(AppFile appFile) {
        this.appFiles.put(appFile.getId(), appFile);
    }

    /**
     * Gets the {@link Book}s stored in the {@link AppFile} with the given id.
     * 
     * @param fileId
     *            the id of the {@link AppFile}.
     * @return the {@link Collection} of {@link Book}s.
     */
    public List<Book> getBooksInFile(String fileId) {
        AppFile appFile = getAppFile(fileId);

        return appFile.getBooks();
    }

    /**
     * Gets the {@link Book} stored in the {@link AppFile} with the given id.
     * 
     * @param fileId
     *            the id of the {@link AppFile}.
     * @param bookId
     *            the id of the {@link Book}.
     * @return the {@link Book} with the given id, or <code>null</code>, if
     *         no such book existed.
     */
    public Book getBooksInFile(String fileId, String bookId) {
        if (bookId == null) {
            throw new NullPointerException("bookId == null");
        }

        AppFile appFile = getAppFile(fileId);

        return appFile.getBook(bookId);
    }

    /**
     * Adds the given {@link Book} to the {@link AppFile} with the given id.
     * 
     * @param fileId
     *            the id of the {@link AppFile}
     * @param book
     *            the {@link Book} to be added.
     */
    public void addBookToFile(String fileId, Book book) {
        if (book == null) {
            throw new NullPointerException("book == null");
        }

        AppFile appFile = getAppFile(fileId);

        appFile.addBook(book);
    }

    /**
     * Removes the {@link Book} with the given id from the {@link AppFile} with
     * the given id.
     * 
     * @param fileId
     *            the id of the {@link AppFile}
     * @param bookId
     *            the id of the {@link Book}.
     */
    public void removeBookFromFile(String fileId, String bookId) {
        if (bookId == null) {
            throw new NullPointerException("bookId == null");
        }

        AppFile appFile = getAppFile(fileId);

        appFile.removeBook(bookId);
    }

    /**
     * Returns, whether or not the {@link AppFile} with the given id, is in it's
     * modified state.
     * 
     * @param fileId
     *            the id of the {@link AppFile}.
     * @return the modified state of the {@link AppFile}.
     */
    public boolean isFileModified(String fileId) {
        AppFile appFile = getAppFile(fileId);

        return appFile.isModified();
    }

    /**
     * Gets the previously set path used to save the {@link AppFile}.
     * 
     * @param fileId
     *            the if of the {@link AppFile}
     * @return the path or <code>null</code>, if the {@link AppFile} was
     *         never saved before.
     */
    public String getPathOfFile(String fileId) {
        AppFile appFile = getAppFile(fileId);

        return appFile.getPath();
    }

    /**
     * Adds the given {@link AppModelModifyListener} to the {@link AppFile} with
     * the given id.
     * 
     * @param fileId
     *            the id of the {@link AppFile}.
     * @param listener
     *            the given {@link AppModelModifyListener}
     */
    public void addAppModelModifyListenerToFile(String fileId,
            AppModelModifyListener listener) {
        if (listener == null) {
            throw new NullPointerException("listener == null");
        }

        AppFile appFile = getAppFile(fileId);

        appFile.addAppModelModifyListener(listener);
    }

    /**
     * Removes the given {@link AppModelModifyListener} from the {@link AppFile}
     * with the given id.
     * 
     * @param fileId
     *            the id of the {@link AppFile}.
     * @param listener
     *            the given {@link AppModelModifyListener}
     */
    public void removeAppModelModifyListener(String fileId,
            AppModelModifyListener listener) {
        if (listener == null) {
            throw new NullPointerException("listener == null");
        }

        AppFile appFile = getAppFile(fileId);

        appFile.removeAppModelModifyListener(listener);
    }

    /**
     * Gets the {@link AppFile} with the given id and handles all exceptions
     * 
     * @param fileId
     *            the id of the {@link AppFile}
     * @return the {@link AppFile};
     */
    private AppFile getAppFile(String fileId) {
        if (fileId == null) {
            throw new NullPointerException("fileId == null");
        }

        AppFile appFile = this.appFiles.get(fileId);

        if (appFile == null) {
            throw new IllegalArgumentException(
                    "No file with the given id in the model!");
        }
        return appFile;
    }

    /**
     * @author Markus Wittlinger
     * @version 1.0 ($Id$)
     */
    public interface AppModelModifyListener {
        /**
         * Notifies, that the modified state of the {@link AppFile} with the
         * given id, has changed.
         * 
         * @param fileId
         *            the id of the {@link AppFile}.
         * @param modified
         *            the new modified state of {@link AppFile}.
         */
        public void appFileModified(String fileId, boolean modified);
    }
}

package org.codecover.simplejavaapp.model;

import java.io.File;
import java.io.FileOutputStream;
import java.io.IOException;
import java.util.Collection;
import java.util.Comparator;
import java.util.Iterator;
import java.util.LinkedList;
import java.util.List;
import java.util.Map;
import java.util.TreeMap;
import java.util.UUID;

import javax.xml.parsers.DocumentBuilder;
import javax.xml.parsers.DocumentBuilderFactory;
import javax.xml.parsers.ParserConfigurationException;
import javax.xml.transform.OutputKeys;
import javax.xml.transform.Transformer;
import javax.xml.transform.TransformerException;
import javax.xml.transform.TransformerFactory;
import javax.xml.transform.dom.DOMSource;
import javax.xml.transform.stream.StreamResult;

import org.codecover.simplejavaapp.model.AppModel.AppModelModifyListener;
import org.codecover.simplejavaapp.model.Book.BookModifyListener;
import org.w3c.dom.Document;
import org.w3c.dom.Element;
import org.w3c.dom.Node;
import org.w3c.dom.NodeList;
import org.xml.sax.SAXException;

/**
 * @author Markus Wittlinger
 * @version 1.0 ($Id$)
 */
public class AppFile {
    private final String id;

    private final Map<String, Book> books;

    private final BookListener bookListener;

    private final List<AppModelModifyListener> modifyListener;

    private boolean modified = false;

    private String path;

    /**
     * Constructor.
     */
    private AppFile() {
        this.id = UUID.randomUUID().toString();
        this.bookListener = new BookListener();
        this.books = new TreeMap<String, Book>(new Comparator<String>() {
            public int compare(String o1, String o2) {
                return o1.compareTo(o2);
            }
        });
        this.modifyListener = new LinkedList<AppModelModifyListener>();
    }

    /**
     * Creates and returns a new {@link AppFile}.
     * 
     * @return the new {@link AppFile}.
     */
    static AppFile newFile() {
        return new AppFile();
    }

    /**
     * Gets all {@link Book Books} contained in this {@link AppFile}.
     * 
     * @return the {@link Collection} of books.
     */
    List<Book> getBooks() {
        return new LinkedList<Book>(this.books.values());
    }

    /**
     * Gets the {@link Book} with the given id.
     * 
     * @param id
     *            the given id.
     * @return the {@link Book} with the given id, or <code>null</code>, if
     *         no such book existed.
     */
    Book getBook(String id) {
        return this.books.get(id);
    }

    /**
     * Adds the given {@link Book} to this {@link AppFile}
     * 
     * @param book
     *            the given {@link Book}, that is to be added.
     */
    void addBook(Book book) {
        this.books.put(book.getId(), book);
        book.setBookModifyListener(this.bookListener);

        setModified(true);
    }

    /**
     * Removes the {@link Book} with the given id.
     * 
     * @param bookId
     *            the id of the book {@link Book}.
     */
    void removeBook(String bookId) {
        this.books.remove(bookId);

        setModified(true);
    }

    /**
     * Gets the id of this {@link AppFile}.
     * 
     * @return the id.
     */
    String getId() {
        return this.id;
    }

    /**
     * Gets the modified.
     * 
     * @return the modified
     */
    boolean isModified() {
        return this.modified;
    }

    /**
     * Sets the modified.
     * 
     * @param modified
     *            the modified to set
     */
    void setModified(boolean modified) {
        this.modified = modified;
        notifyListeners();
    }

    /**
     * Adds the given {@link AppModelModifyListener} to the list of listeners
     * 
     * @param listener
     *            the given {@link AppModelModifyListener}
     */
    void addAppModelModifyListener(AppModelModifyListener listener) {
        this.modifyListener.add(listener);
    }

    /**
     * Removes the given {@link AppModelModifyListener} from the list of
     * listeners
     * 
     * @param listener
     *            the given {@link AppModelModifyListener}
     */
    void removeAppModelModifyListener(AppModelModifyListener listener) {
        this.modifyListener.remove(listener);
    }

    private void notifyListeners() {
        for (AppModelModifyListener listener : this.modifyListener) {
            listener.appFileModified(getId(), isModified());
        }
    }

    /**
     * Sets the path.
     * 
     * @param path
     *            the path to set
     */
    private void setPath(String path) {
        this.path = path;
        notifyListeners();
    }

    /**
     * Gets the path.
     * 
     * @return the path
     */
    String getPath() {
        return this.path;
    }

    /**
     * Saves this {@link AppFile} as a XML file to the given location.
     * 
     * @param file
     *            the given location.
     * @throws ParserConfigurationException
     * @throws TransformerException
     * @throws IOException
     */
    void saveAppFile(File file) throws ParserConfigurationException,
            TransformerException, IOException {
        Document xmlDoc = null;
        DocumentBuilderFactory docBFac;
        DocumentBuilder docBuild;

        docBFac = DocumentBuilderFactory.newInstance();
        docBFac.setNamespaceAware(true);
        docBuild = docBFac.newDocumentBuilder();
        xmlDoc = docBuild.newDocument();

        if (xmlDoc != null) {
            Element documentElement = xmlDoc.createElement("SimpleJavaApp");
            xmlDoc.appendChild(documentElement);

            Element bookListElement = xmlDoc.createElement("BookList");

            for (Book book : getBooks()) {
                bookListElement.appendChild(book.getXMLRepresentation(xmlDoc));
            }

            documentElement.appendChild(bookListElement);
        }

        FileOutputStream out = new FileOutputStream(file);
        try {
            DOMSource domSource = new DOMSource(xmlDoc);
            StreamResult streamResult = new StreamResult(out);
            TransformerFactory tf = TransformerFactory.newInstance();
            Transformer serializer = tf.newTransformer();
            serializer.setOutputProperty(OutputKeys.ENCODING, "UTF-8");
            serializer.setOutputProperty(OutputKeys.INDENT, "yes");
            serializer.transform(domSource, streamResult);
            out.flush();

            /*
             * All saving operations were successful, so the AppFile can be set
             * to 'not modified'.
             */
            this.setModified(false);
            this.setPath(file.getAbsolutePath());
        } finally {
            out.close();
        }
    }

    /**
     * Loads an {@link AppFile} from the given location.
     * 
     * @param file
     *            the location of the persistently saved {@link AppFile}
     * @return the read {@link AppFile}.
     * @throws ParserConfigurationException
     * @throws SAXException
     * @throws IOException
     */
    static AppFile loadAppFile(File file) throws ParserConfigurationException,
            SAXException, IOException {
        AppFile appFile = AppFile.newFile();

        DocumentBuilderFactory factory = DocumentBuilderFactory.newInstance();
        factory.setNamespaceAware(true);
        DocumentBuilder DocumentBuilder = factory.newDocumentBuilder();
        Document xmlDoc = DocumentBuilder.parse(file);

        Element element = xmlDoc.getDocumentElement();

        NodeListIterable nodeListIterable = appFile.new NodeListIterable(
                element.getElementsByTagName("BookList"));
        for (Node node : nodeListIterable) {
            for (Node subNode : appFile.new NodeListIterable(((Element) node)
                    .getElementsByTagName("Book"))) {
                appFile.addBook(Book.getBookFromXML((Element) subNode));
            }
        }

        appFile.setModified(false);
        appFile.setPath(file.getAbsolutePath());

        return appFile;
    }

    private final class NodeListIterable implements Iterable<Node> {
        private final List<Node> list;

        /**
         * Initializes this class with the {@link NodeList} it is supposed to
         * encapsulate.
         * 
         * @param nodeList
         *            the {@link NodeList} to encapsulate.
         */
        public NodeListIterable(NodeList nodeList) {
            this.list = new LinkedList<Node>();

            for (int i = 0; i < nodeList.getLength(); i++) {
                this.list.add(nodeList.item(i));
            }
        }

        /**
         * (non-Javadoc)
         * 
         * @see java.lang.Iterable#iterator()
         */
        public Iterator<Node> iterator() {
            return this.list.iterator();
        }
    }

    private final class BookListener implements BookModifyListener {

        /**
         * (non-Javadoc)
         * 
         * @see org.codecover.simplejavaapp.model.Book.BookModifyListener#stateModified()
         */
        public void stateModified() {
            AppFile.this.setModified(true);
        }
    }
}

package org.codecover.simplejavaapp.controller;

import java.io.File;
import java.io.IOException;

import javax.swing.JOptionPane;
import javax.xml.parsers.ParserConfigurationException;
import javax.xml.transform.TransformerException;

import org.codecover.simplejavaapp.model.AppModel;
import org.codecover.simplejavaapp.model.Book;
import org.codecover.simplejavaapp.view.AppView;
import org.codecover.simplejavaapp.view.AppView.ViewListener;
import org.xml.sax.SAXException;

/**
 * @author Markus Wittlinger
 * @version 1.0 ($Id$)
 */
public class AppController {
    private static final AppModel model = AppModel.getInstance();

    private static final AppView view = AppView.getInstance();

    private static AppController instance;

    /**
     * Singleton-Pattern method to get the instance of {@link AppController}.
     * 
     * @return the instance of the {@link AppController}
     */
    public static AppController getInstance() {
        if (instance == null) {
            instance = new AppController();
        }

        return instance;
    }

    private AppController() {
        view.adViewListener(new AppViewListener());
    }

    /**
     * Opens the application in its initial state.
     */
    public static void initialize() {
        AppController appController = AppController.getInstance();

        appController.onOpenNewWindow();
    }

    private void onOpenNewWindow() {
        String fileId = model.newFile();

        view.openWindow(fileId);
    }

    private void onNewBook(String fileId) {
        Book book = new Book();

        model.addBookToFile(fileId, book);
    }

    private boolean onCloseWindow(String fileId) {
        if (model.isFileModified(fileId)) {
            int result = view.showConfirmationDialog(
                    "The file has modifications. Save before proceeding?",
                    fileId);

            switch (result) {
                case JOptionPane.YES_OPTION:
                    if (!onSave(fileId)) {
                        /* Saving was aborted or failed, so we don't proceed */
                        return false;
                    }
                    break;
                case JOptionPane.NO_OPTION:
                    break;
                case JOptionPane.CANCEL_OPTION:
                    /* Saving was aborted or failed, so we don't proceed */
                    return false;
            }
        }

        view.closeWindow(fileId);
        model.closeFile(fileId, true);
        return true;
    }

    private void onDelete(String fileId, String[] bookIds) {
        if(bookIds.length == 0) {
            return;
        }

        int result = view.showConfirmationDialog(
                "Are you sure you want to delete these books?", fileId);
        switch (result) {
            case JOptionPane.YES_OPTION:
                for (String bookId : bookIds) {
                    model.removeBookFromFile(fileId, bookId);
                }
                break;
            case JOptionPane.NO_OPTION:
            case JOptionPane.CANCEL_OPTION:
        }
    }

    private void onOpen(String fileId) {
        if (model.isFileModified(fileId)) {
            int result = view.showConfirmationDialog(
                    "The file has modifications. Save before proceeding?",
                    fileId);

            switch (result) {
                case JOptionPane.YES_OPTION:
                    if (!onSave(fileId)) {
                        /* Saving was aborted or failed, so we don't proceed */
                        return;
                    }
                    break;
                case JOptionPane.NO_OPTION:
                    break;
                case JOptionPane.CANCEL_OPTION:
                    /* Saving was aborted or failed, so we don't proceed */
                    return;
            }
        }

        String path = view.showOpenDialog(fileId);

        if (path == null) {
            return;
        }

        try {
            String newFileId = model.loadFile(new File(path));

            view.openFileInWindow(fileId, newFileId);
            model.closeFile(fileId, true);
        } catch (ParserConfigurationException e) {
            view.showErrorDialog("Error loading file", fileId);
        } catch (SAXException e) {
            view.showErrorDialog("Error loading file", fileId);
        } catch (IOException e) {
            view.showErrorDialog("Error loading file", fileId);
        }
    }

    private void onQuit(String fileId, String[] allFileIds) {
        for (String id : allFileIds) {
            if (!onCloseWindow(id)) {
                return;
            }
        }

        /* All windows were closed so we can exit the application. */
        System.exit(0);
    }

    private boolean onSave(String fileId) {
        String path = model.getPathOfFile(fileId);

        if (path == null) {
            return onSaveAs(fileId);
        } else {
            save(fileId, new File(path));
            return true;
        }
    }

    private boolean onSaveAs(String fileId) {
        String path = view.showSaveAsDialog(fileId);

        if (path == null) {
            return false;
        } else {
            save(fileId, new File(path));
            return true;
        }
    }

    private void save(String fileId, File file) {
        try {
            model.saveFile(fileId, file);
        } catch (ParserConfigurationException e) {
            view.showErrorDialog("Error saving file", fileId);
        } catch (TransformerException e) {
            view.showErrorDialog("Error saving file", fileId);
        } catch (IOException e) {
            view.showErrorDialog("Error saving file", fileId);
        }
    }

    private final class AppViewListener implements ViewListener {

        /**
         * (non-Javadoc)
         * 
         * @see org.codecover.simplejavaapp.view.AppView.ViewListener#update(org.codecover.simplejavaapp.view.AppView.ViewListener.ViewEvent,
         *      java.lang.String, java.lang.Object)
         */
        public void update(ViewEvent viewEvent, String fileId, Object args) {
            switch (viewEvent) {
                case EVENT_CLOSE_WINDOW:
                    onCloseWindow(fileId);
                    break;
                case EVENT_DELETE:
                    onDelete(fileId, (String[]) args);
                    break;
                case EVENT_NEW_BOOK:
                    onNewBook(fileId);
                    break;
                case EVENT_NEW_WINDOW:
                    onOpenNewWindow();
                    break;
                case EVENT_OPEN:
                    onOpen(fileId);
                    break;
                case EVENT_QUIT:
                    onQuit(fileId, (String[]) args);
                    break;
                case EVENT_SAVE:
                    onSave(fileId);
                    break;
                case EVENT_SAVE_AS:
                    onSaveAs(fileId);
                    break;
            }
        }
    }
}

package org.codecover.simplejavaapp;

import org.codecover.simplejavaapp.controller.AppController;

/**
 * @author Markus Wittlinger
 * @version 1.0 ($Id$)
 */
public class SimpleJavaApp {
    final static boolean thisIsAMac = System.getProperty("mrj.version") != null;

    /**
     * @param args
     */
    public static void main(String[] args) {
        if (thisIsAMac) {
            System.setProperty("apple.laf.useScreenMenuBar", "true");
        }

        AppController.initialize();
    }
}
